import { animated } from '@react-spring/web'
import { useTheme } from '@nivo/core'
import { CircularAxisTickProps } from './types'

export const CircularAxisTick = ({ label, animated: animatedProps }: CircularAxisTickProps) => {
    const theme = useTheme()

    return (
        <animated.g opacity={animatedProps.opacity}>
            <animated.line
                x1={animatedProps.x1}
                y1={animatedProps.y1}
                x2={animatedProps.x2}
                y2={animatedProps.y2}
                style={theme.axis.ticks.line}
            />
            <animated.text
                dx={animatedProps.textX}
                dy={animatedProps.textY}
                dominantBaseline="central"
                style={theme.axis.ticks.text}
                textAnchor="middle"
            >
                {label}
            </animated.text>
        </animated.g>
    )
}
import { createElement, SVGProps, useMemo } from 'react'
import { useSpring, useTransition } from '@react-spring/web'
import { useMotionConfig, useTheme, positionFromAngle, degreesToRadians } from '@nivo/core'
import { AnyScale, getScaleTicks } from '@nivo/scales'
import { ArcLine } from '@nivo/arcs'
import { CircularAxisConfig, CircularAxisTickAnimatedProps } from './types'
import { CircularAxisTick } from './CircularAxisTick'

type CircularAxisProps = {
    type: 'inner' | 'outer'
    center?: [number, number]
    radius: number
    startAngle: number
    endAngle: number
    scale: AnyScale
} & CircularAxisConfig

const getLinePositions = (angle: number, innerRadius: number, outerRadius: number) => {
    const start = positionFromAngle(degreesToRadians(angle), innerRadius)
    const end = positionFromAngle(degreesToRadians(angle), outerRadius)

    return {
        x1: start.x,
        y1: start.y,
        x2: end.x,
        y2: end.y,
    }
}

const getTextPosition = (angle: number, radius: number) => {
    const position = positionFromAngle(degreesToRadians(angle), radius)

    return {
        textX: position.x,
        textY: position.y,
    }
}

export const CircularAxis = ({
    type,
    center = [0, 0],
    radius,
    startAngle: originalStartAngle,
    endAngle: originalEndAngle,
    scale,
    tickSize = 5,
    tickPadding = 12,
    tickComponent = CircularAxisTick,
}: CircularAxisProps) => {
    const startAngle = originalStartAngle - 90
    const endAngle = originalEndAngle - 90

    const theme = useTheme()

    const { animate, config: springConfig } = useMotionConfig()
    const spring = useSpring<{
        radius: number
        startAngle: number
        endAngle: number
        opacity: number
    }>({
        radius,
        startAngle,
        endAngle,
        opacity: 1,
        immediate: !animate,
        config: springConfig,
    })

    const ticks = useMemo(() => {
        const values = getScaleTicks(scale)

        return values.map((value, index) => ({
            key: index,
            label: value,
            angle: scale(value) - 90,
        }))
    }, [scale])

    const outerRadius = type === 'inner' ? radius - tickSize : radius + tickSize
    const textRadius = type === 'inner' ? outerRadius - tickPadding : outerRadius + tickPadding

    const transition = useTransition<typeof ticks[0], CircularAxisTickAnimatedProps>(ticks, {
        keys: tick => tick.key,
        initial: tick => ({
            ...getLinePositions(tick.angle, radius, outerRadius),
            ...getTextPosition(tick.angle, textRadius),
            opacity: 1,
        }),
        from: tick => ({
            ...getLinePositions(tick.angle, radius, outerRadius),
            ...getTextPosition(tick.angle, textRadius),
            opacity: 0,
        }),
        enter: tick => ({
            ...getLinePositions(tick.angle, radius, outerRadius),
            ...getTextPosition(tick.angle, textRadius),
            opacity: 1,
        }),
        update: tick => ({
            ...getLinePositions(tick.angle, radius, outerRadius),
            ...getTextPosition(tick.angle, textRadius),
            opacity: 1,
        }),
        leave: tick => ({
            ...getLinePositions(tick.angle, radius, outerRadius),
            ...getTextPosition(tick.angle, textRadius),
            opacity: 0,
        }),
        immediate: !animate,
        config: springConfig,
    })

    return (
        <g transform={`translate(${center[0]}, ${center[1]})`}>
            <ArcLine
                animated={spring}
                {...(theme.axis.domain.line as Omit<SVGProps<SVGPathElement>, 'ref'>)}
                fill="none"
            />
            {transition((animatedProps, tick) =>
                createElement(tickComponent, {
                    key: tick.key,
                    label: tick.label,
                    animated: animatedProps,
                })
            )}
        </g>
    )
}
import { SVGProps, useMemo } from 'react'
import { useTransition, animated } from '@react-spring/web'
import { useMotionConfig, useTheme } from '@nivo/core'
import { AnyScale, getScaleTicks } from '@nivo/scales'

interface PolarGridProps {
    scale: AnyScale
    ticks?: number | number[]
    innerRadius: number
    outerRadius: number
}

export const RadialGrid = ({ scale, innerRadius, outerRadius }: PolarGridProps) => {
    const theme = useTheme()

    const angles = useMemo(() => {
        const values = getScaleTicks(scale)

        return values.map((angleValue, index) => ({
            id: index,
            angle: scale(angleValue) - 90,
        }))
    }, [scale])

    const { animate, config: springConfig } = useMotionConfig()
    const transition = useTransition<
        { id: number; angle: number },
        { angle: number; opacity: number }
    >(angles, {
        keys: item => item.id,
        initial: item => ({
            angle: item.angle,
            opacity: 1,
        }),
        from: item => ({
            angle: item.angle,
            opacity: 0,
        }),
        enter: item => ({
            angle: item.angle,
            opacity: 1,
        }),
        update: item => ({
            angle: item.angle,
            opacity: 1,
        }),
        leave: item => ({
            angle: item.angle,
            opacity: 0,
        }),
        config: springConfig,
        immediate: !animate,
    })

    return (
        <>
            {transition((style, angle) => (
                <animated.g
                    key={angle.id}
                    transform={style.angle.to(v => `rotate(${v})`)}
                    opacity={style.opacity}
                >
                    <line
                        x1={innerRadius}
                        x2={outerRadius}
                        {...(theme.grid.line as SVGProps<SVGLineElement>)}
                    />
                </animated.g>
            ))}
        </>
    )
}
import { SVGProps, useMemo } from 'react'
import { useTransition } from '@react-spring/web'
import { useTheme, useMotionConfig } from '@nivo/core'
import { AnyScale, getScaleTicks } from '@nivo/scales'
import { ArcLine } from '@nivo/arcs'

interface CircularGridProps {
    scale: AnyScale
    startAngle: number
    endAngle: number
}

export const CircularGrid = ({
    scale,
    startAngle: originalStartAngle,
    endAngle: originalEndAngle,
}: CircularGridProps) => {
    const theme = useTheme()

    const startAngle = originalStartAngle - 90
    const endAngle = originalEndAngle - 90

    const radii = useMemo(() => {
        const values = getScaleTicks(scale)

        return values.map((value, index) => {
            let radius = scale(value) as number
            if ('bandwidth' in scale) {
                radius += scale.bandwidth() / 2
            }

            return {
                id: index,
                radius,
            }
        })
    }, [scale])

    const { animate, config: springConfig } = useMotionConfig()
    const transition = useTransition<
        { id: number; radius: number },
        { radius: number; startAngle: number; endAngle: number; opacity: number }
    >(radii, {
        keys: item => item.id,
        initial: item => ({
            radius: item.radius,
            startAngle,
            endAngle,
            opacity: 1,
        }),
        from: item => ({
            radius: item.radius,
            startAngle,
            endAngle,
            opacity: 0,
        }),
        enter: item => ({
            radius: item.radius,
            startAngle,
            endAngle,
            opacity: 1,
        }),
        update: item => ({
            radius: item.radius,
            startAngle,
            endAngle,
            opacity: 1,
        }),
        leave: item => ({
            radius: item.radius,
            startAngle,
            endAngle,
            opacity: 0,
        }),
        config: springConfig,
        immediate: !animate,
    })

    return (
        <>
            {transition((style, item) => (
                <ArcLine
                    key={item.id}
                    animated={style}
                    {...(theme.grid.line as Omit<SVGProps<SVGPathElement>, 'ref'>)}
                    strokeOpacity={style.opacity}
                    fill="none"
                />
            ))}
        </>
    )
}
import { AnyScale } from '@nivo/scales'
import { RadialGrid } from './RadialGrid'
import { CircularGrid } from './CircularGrid'

interface PolarGridProps {
    center: [number, number]
    enableRadialGrid: boolean
    enableCircularGrid: boolean
    angleScale: AnyScale
    radiusScale: AnyScale
    startAngle: number
    endAngle: number
}

export const PolarGrid = ({
    center,
    enableRadialGrid,
    enableCircularGrid,
    angleScale,
    radiusScale,
    startAngle,
    endAngle,
}: PolarGridProps) => {
    const innerRadius = Math.min(...radiusScale.range())
    const outerRadius = Math.max(...radiusScale.range())

    return (
        <g transform={`translate(${center[0]},${center[1]})`}>
            {enableRadialGrid && (
                <RadialGrid
                    scale={angleScale}
                    innerRadius={innerRadius}
                    outerRadius={outerRadius}
                />
            )}
            {enableCircularGrid && (
                <CircularGrid scale={radiusScale} startAngle={startAngle} endAngle={endAngle} />
            )}
        </g>
    )
}
